textarea.value_entered:
// gridsize 1 6 java 0.3
Node head = Utils.createList( 'A','A','B','A','C','A',"tail" );
Node p = null; // 0 2
while ((head != null) && (head.info =='A')) {
 head = head.next;
}
if (head == null)
{
 tail = null;
}
else
{
 for( p = head; p.next != null; )
 {
 if (p.next.info == 'A') {
 p.next = p.next.next;
 }
 else {
 p = p.next;
 }
 }
}
tail = p;
